//配置路由
import Vue from 'vue'
import VueRouter from 'vue-router'
//使用插件
Vue.use(VueRouter);
//引入路由组件

import Home from "@/pages/Home"
import Login from "@/pages/Login"
import Search from "@/pages/Search"
import Register from "@/pages/Register"

// import  Home from '@/pages/Home'
//配置路由，首先暴露
 let router =new VueRouter(

    {
        //配置路由规则
        routes:[
            {
                path:"/home",
                component:Home,
                meta:{show:true}
            },
            {
                path:"/login",
                component:Login,
                meta:{show:false}
            },
            {
                path:"/search",
                component:Search,
                meta:{show:true}
            },
            {
                path:"/register",
                component:Register,
                meta:{show:false}
            },
            {
                path: "*",
                redirect: "/Home", //一上来就展示首页
            },
        ]
    
    }

)
export default router
