//在当前模块对于api进行统一管理
import requests from "./request";
//三级联动
export const reqCategoryList = async () => {
    try {
        const response = await requests.request({ url: '/product/getBaseCategoryList', method: 'get' });
        console.log(response);
    } catch (error) {
        console.error('请求失败：', error);
    }
};
